if ($_SERVER["REQUEST_METHOD"] !== "POST")
	exit();

require_once("Classes/InputManager.php");
require_once("Classes/DatabaseConnection.php");
require_once("Classes/User.php");
require_once("Classes/Modal.php");

validateInput();
logUserIn();

Location::navigate("Index.php");

function validateInput(): void {
	$SAFE_VARIABLE_NAMES = ["email"];
	$VARIABLE_NAMES      = [...$SAFE_VARIABLE_NAMES, "password"];
	
	if (!InputManager::areAllSet(ArrayName::Post, ...$VARIABLE_NAMES))
		Modal::displayAndExit("Certain form variables are missing. Please re-enter your information and submit the form again.");
	
	$assertion = InputManager::validateInput(
		new InputInfo($_POST["email"], "Email", 7, 254, User::EMAIL_REGEX_PATTERN, "The entered email is invalid."),
		new InputInfo($_POST["password"], "Password", 8, 20)
	);
	
	if (!$assertion->isTrue())
		Modal::displayAndExit($assertion->getFailureMessage());
	
	InputManager::sanitizeAll(ArrayName::Post, ...$SAFE_VARIABLE_NAMES);
}

function logUserIn(): void {
	try {
		$connection = new DatabaseConnection();
		$result = $connection->query("SELECT * FROM user WHERE Email = ?;", "s", $_POST["email"]);
		
		if ($result->num_rows === 0)
			Modal::displayAndExit("No user with such email address exists.");
		
		$row = $result->fetch_assoc();
		
		if (!password_verify($_POST["password"], $row["Password"]))
			Modal::displayAndExit("The entered password is incorrect.");
		
		$_SESSION[User::class] = new User($row["Username"], $row["Email"], $row["Password"], false);
	}
	catch (DatabaseException $ex) {
		Modal::displayAndExit("An error occurred while trying to communicate with the database ({$ex->getMessage()}).");
	}
	finally {
		if (isset($connection)) {
			$connection->disconnect();
		}
	}
}
